        //             </ul>
        //             <hr />
        //         </div>
        //     }
        // </div>

        <div>
            <img src={movies.medium_cover_image} alt={movies.title} />
            <h2>
                {movies.title}
            </h2>
            <p>
                {movies.description_full ? movies.description_full : "undifined"}
            </p>
            {/* <ul>
                {movies.genres.map(g => <li key={g}>{g}</li>)}
            </ul> */}
            <hr />
        </div>

    )
}

export default Detail;
